const { app, BrowserWindow, ipcMain, Tray, Menu, nativeImage, globalShortcut } = require('electron');
const path = require('path');
const spawn = require('cross-spawn');

let mainWindow;
let quickAddWindow;
let tray;
let fastApiProcess;

function createWindow() {
  // Determine icon path for development vs production
  const iconPath = app.isPackaged
    ? path.join(process.resourcesPath, 'build', 'icon.png')
    : path.join(__dirname, '..', 'build', 'icon.png');

  mainWindow = new BrowserWindow({
    width: 1200,
    height: 800,
    frame: false,  // Remove default title bar
    backgroundColor: '#fafafa', // Matches light theme background
    show: false, // Don't show until ready
    icon: iconPath, // Set window icon
    webPreferences: {
      preload: path.join(__dirname, 'preload.js'),
      contextIsolation: true,
      nodeIntegration: false
    }
  });

  // Load from Vite dev server in development, or from built files in production
  const isDev = process.argv.includes('--dev');

  if (isDev) {
    mainWindow.loadURL('http://localhost:5173');
    mainWindow.webContents.openDevTools();
  } else {
    // In production, use process.resourcesPath for correct path
    const indexPath = path.join(process.resourcesPath, 'app.asar.unpacked', 'frontend', 'dist', 'index.html');
    
    console.log('=== TRAK Frontend Loading ===');
    console.log('Resources path:', process.resourcesPath);
    console.log('Loading from:', indexPath);
    
    // Use loadFile instead of loadURL for better compatibility
    mainWindow.loadFile(indexPath).then(() => {
      console.log('✓ Frontend loaded successfully!');
    }).catch(err => {
      console.error('✗ Failed to load frontend:', err);
      mainWindow.loadURL(`data:text/html,<h1>Error Loading TRAK</h1><p><strong>Tried path:</strong> ${indexPath}</p><p><strong>Error:</strong> ${err.message}</p><p><strong>Please reinstall the app</strong></p>`);
    });
    
    // Open DevTools for debugging (remove this line for production)
    mainWindow.webContents.openDevTools();
  }

  // Show window when ready
  mainWindow.once('ready-to-show', () => {
    mainWindow.show();
  });

  // Minimize to tray instead of taskbar
  mainWindow.on('minimize', (event) => {
    event.preventDefault();
    mainWindow.hide();
  });

  // Don't close app, just hide to tray
  mainWindow.on('close', (event) => {
    if (!app.isQuitting) {
      event.preventDefault();
      mainWindow.hide();
    }
  });
}

function createQuickAddWindow() {
  quickAddWindow = new BrowserWindow({
    width: 420,
    height: 60,
    frame: false,
    resizable: false,
    transparent: true,
    alwaysOnTop: true,
    skipTaskbar: true,
    backgroundColor: '#00000000',
    webPreferences: {
      preload: path.join(__dirname, 'preload.js'),
      contextIsolation: true,
      nodeIntegration: false
    }
  });

  // Load the dedicated quick-add HTML file
  quickAddWindow.loadFile(path.join(__dirname, '..', 'frontend', 'quick-add.html'));

  // Hide instead of close
  quickAddWindow.on('blur', () => {
    quickAddWindow.hide();
  });

  quickAddWindow.hide();
}

function createTray() {
  // Create a simple icon (you can replace this with an actual icon file later)
  const icon = nativeImage.createFromDataURL('data:image/png;base64,');

  tray = new Tray(icon);

  const contextMenu = Menu.buildFromTemplate([
    {
      label: 'New',
      accelerator: 'CmdOrCtrl+N',
      click: () => {
        showQuickAddWindow();
      }
    },
    {
      label: 'Continue',
      accelerator: 'CmdOrCtrl+O',
      enabled: false, // Will implement later
    },
    {
      label: 'Stop',
      accelerator: 'CmdOrCtrl+S',
      enabled: false, // Will implement later
    },
    { type: 'separator' },
    {
      label: 'Sync',
      accelerator: 'F5',
      enabled: false, // Future feature
    },
    {
      label: 'Reports',
      click: () => {
        mainWindow.show();
        mainWindow.webContents.send('navigate-to', '/analytics');
      }
    },
    { type: 'separator' },
    {
      label: 'Preferences',
      accelerator: 'CmdOrCtrl+,',
      click: () => {
        mainWindow.show();
        mainWindow.webContents.send('navigate-to', '/settings');
      }
    },
    {
      label: 'Export/Import',
      click: () => {
        mainWindow.show();
        mainWindow.webContents.send('navigate-to', '/settings');
      }
    },
    { type: 'separator' },
    {
      label: 'Use manual mode',
      accelerator: 'CmdOrCtrl+D',
      type: 'checkbox',
      checked: false,
      enabled: false, // Future feature
    },
    {
      label: 'Use mini timer',
      accelerator: 'CmdOrCtrl+M',
      type: 'checkbox',
      checked: false,
      click: () => {
        showQuickAddWindow();
      }
    },
    { type: 'separator' },
    {
      label: 'Apps',
      enabled: false,
      submenu: [
        { label: 'No integrations available' }
      ]
    },
    {
      label: 'Send Feedback',
      enabled: false,
    },
    {
      label: 'Check for updates...',
      enabled: false,
    },
    { type: 'separator' },
    {
      label: 'Log out',
      enabled: false,
    },
    { type: 'separator' },
    {
      label: 'Quit',
      accelerator: 'CmdOrCtrl+Q',
      click: () => {
        app.isQuitting = true;
        app.quit();
      }
    }
  ]);

  tray.setToolTip('TRAK - Time Tracker');
  tray.setContextMenu(contextMenu);

  // Double-click to show main window
  tray.on('double-click', () => {
    mainWindow.show();
  });

  // Single click to show quick add
  tray.on('click', () => {
    showQuickAddWindow();
  });
}

function showQuickAddWindow() {
  if (!quickAddWindow) {
    createQuickAddWindow();
  }

  // Get the primary display or the one where cursor is
  const { screen } = require('electron');
  const cursorPosition = screen.getCursorScreenPoint();
  const display = screen.getDisplayNearestPoint(cursorPosition);

  // Get work area (excludes taskbar/dock)
  const workArea = display.workArea;

  // Position in bottom-right corner, accounting for taskbar
  const windowWidth = 420;
  const windowHeight = 60;
  const padding = 10; // Space from edges

  const x = workArea.x + workArea.width - windowWidth - padding;
  const y = workArea.y + workArea.height - windowHeight - padding;

  quickAddWindow.setPosition(x, y);
  quickAddWindow.setAlwaysOnTop(true, 'screen-saver'); // Highest level
  quickAddWindow.show();
  quickAddWindow.focus();
}

function registerGlobalShortcuts() {
  // Global shortcut to open quick add window
  const ret = globalShortcut.register('CommandOrControl+Shift+A', () => {
    showQuickAddWindow();
  });

  if (!ret) {
    console.log('Shortcut registration failed');
  }

  console.log('Global shortcut registered: Ctrl+Shift+A (Cmd+Shift+A on Mac)');
}

function startFastAPI() {
  console.log('Starting FastAPI server...');

  // In production, backend might be in different location
  const backendPath = app.isPackaged
    ? path.join(process.resourcesPath, 'backend', 'main.py')
    : path.join(__dirname, '..', 'backend', 'main.py');

  console.log('Backend path:', backendPath);

  // Spawn Python FastAPI process
  fastApiProcess = spawn('python', [backendPath], {
    stdio: 'pipe',
    windowsHide: true  // Hide console window on Windows
  });

  // Optional: Log FastAPI output to Electron console
  fastApiProcess.stdout.on('data', (data) => {
    console.log(`[FastAPI] ${data.toString().trim()}`);
  });

  fastApiProcess.stderr.on('data', (data) => {
    console.error(`[FastAPI Error] ${data.toString().trim()}`);
  });

  fastApiProcess.on('error', (err) => {
    console.error('Failed to start FastAPI:', err);
  });

  fastApiProcess.on('close', (code) => {
    console.log(`FastAPI process exited with code ${code}`);
  });
}

app.whenReady().then(() => {
  startFastAPI();

  // Give FastAPI a moment to start
  setTimeout(() => {
    createWindow();
    createTray();
    registerGlobalShortcuts();
  }, 2000);

  app.on('activate', () => {
    if (mainWindow) {
      mainWindow.show();
    } else {
      createWindow();
    }
  });
});

app.on('window-all-closed', (e) => {
  e.preventDefault();
  // Don't quit - keep running in tray
});

app.on('before-quit', () => {
  app.isQuitting = true;
});

app.on('quit', () => {
  // Unregister all shortcuts
  globalShortcut.unregisterAll();

  // Kill FastAPI process when app closes
  if (fastApiProcess) {
    fastApiProcess.kill();
  }
});

// IPC handlers for window controls
ipcMain.on('window-minimize', () => {
  if (mainWindow) {
    mainWindow.minimize();
  }
});

ipcMain.on('window-maximize', () => {
  if (mainWindow) {
    if (mainWindow.isMaximized()) {
      mainWindow.unmaximize();
    } else {
      mainWindow.maximize();
    }
  }
});

ipcMain.on('window-close', () => {
  if (mainWindow) {
    mainWindow.close();
  }
});

